const mongoose = require('mongoose');
const horseSchema = new mongoose.Schema({
  seller: {
    type: mongoose.Schema.Types.ObjectId,
    ref: 'Seller',
    required: true
  },
  name: {
    type: String,
    required: true,
    trim: true
  },
  breed: {
    type: String,
    required: true
  },
  age: {
    years: {
      type: Number,
      required: true,
      min: 0
    },
    months: {
      type: Number,
      required: true,
      min: 0,
      max: 11
    }
  },
  gender: {
    type: String,
    required: true,
    enum: ['Stallion', 'Mare', 'Gelding']
  },
  color: {
    type: String,
    required: true
  },
  price: {
    type: Number,
    required: true,
    min: 0
  },
  description: {
    type: String,
    required: true
  },
  location: {
    state: {
      type: String,
      required: true
    },
    city: {
      type: String,
      required: true
    },
    pincode: {
      type: String,
      required: true
    }
  },
  images: [{
    url: {
      type: String,
      required: true
    },
    public_id: {
      type: String,
      required: true
    },
    thumbnail_url: String,
    width: Number,
    height: Number,
    format: String
  }],
  specifications: {
    training: {
      type: String,
      required: true,
      enum: ['Basic', 'Intermediate', 'Advanced']
    },
    discipline: [{
      type: String,
      required: true
    }],
    temperament: {
      type: String,
      required: true
    },
    healthStatus: {
      type: String,
      required: true
    },
    vaccination: {
      type: Boolean,
      default: false
    },
    papers: {
      type: Boolean,
      default: false
    }
  },
  listingStatus: {
    type: String,
    enum: ['draft', 'active', 'sold', 'inactive'],
    default: 'draft'
  },
  statistics: {
    views: {
      type: Number,
      default: 0
    },
    inquiries: {
      type: Number,
      default: 0
    },
    lastViewed: {
      type: Date,
      default: Date.now
    }
  },
  boost: {
    active: {
      type: Boolean,
      default: false
    },
    startDate: Date,
    endDate: Date
  },
  verificationStatus: {
    type: String,
    enum: ['pending', 'verified', 'rejected', 'unverified'],
    default: 'unverified'
  },
  verificationDetails: {
    submittedAt: Date,
    submittedBy: {
      type: mongoose.Schema.Types.ObjectId,
      ref: 'User'
    },
    verifiedAt: Date,
    verifiedBy: {
      type: mongoose.Schema.Types.ObjectId,
      ref: 'User'
    },
    documents: [{
