      });
    }
    const review = {
      user: req.user._id,
      rating,
      comment,
      date: Date.now()
    };
    seller.reviews.push(review);

    // Update average rating
    const totalRating = seller.reviews.reduce((sum, item) => sum + item.rating, 0);
    seller.rating = totalRating / seller.reviews.length;
    await seller.save();
    res.json({
