const Horse = require('../models/Horse');
const User = require('../models/User');
const ActivityLog = require('../models/ActivityLog');

// Helper function to build search query
const buildSearchQuery = filters => {
  const query = {
    listingStatus: 'active'
  };
  if (filters.breed) {
    query.breed = {
      $in: Array.isArray(filters.breed) ? filters.breed : [filters.breed]
    };
  }
  if (filters.price) {
    query.price = {
      $gte: filters.price.min || 0,
      $lte: filters.price.max || Number.MAX_SAFE_INTEGER
    };
  }
  if (filters.age) {
    query['age.years'] = {
      $gte: filters.age.min || 0,
      $lte: filters.age.max || Number.MAX_SAFE_INTEGER
    };
  }
  if (filters.location) {
    if (filters.location.state) {
      query['location.state'] = filters.location.state;
    }
    if (filters.location.city) {
      query['location.city'] = filters.location.city;
    }
  }
  if (filters.gender) {
    query.gender = {
      $in: Array.isArray(filters.gender) ? filters.gender : [filters.gender]
    };
  }
  if (filters.specifications) {
    Object.entries(filters.specifications).forEach(([key, value]) => {
      if (value) {
        query[`specifications.${key}`] = value;
      }
    });
  }
  return query;
};

// @desc    Advanced search with filters
// @route   POST /api/search
// @access  Public
exports.advancedSearch = async (req, res) => {
  try {
    const {
      filters,
      sort = {
        createdAt: -1
      },
      page = 1,
      limit = 10
    } = req.body;
    const query = buildSearchQuery(filters);
    const skip = (page - 1) * limit;

    // Execute search with pagination
    const [horses, total] = await Promise.all([Horse.find(query).sort(sort).skip(skip).limit(limit).populate('seller', 'businessName location rating'), Horse.countDocuments(query)]);

    // Log search activity if user is authenticated
    if (req.user) {
      await ActivityLog.create({
        user: req.user._id,
        action: 'SEARCH',
        details: {
          filters,
          resultsCount: horses.length,
          page
        }
      });

      // Update user preferences based on search
      await User.findByIdAndUpdate(req.user._id, {
        $set: {
          'preferences.lastSearch': {
            filters,
            timestamp: new Date()
          }
        }
      });
    }
    res.json({
      success: true,
      data: {
