    },
    lastMessage: {
        content: String,
        sender: {
            type: mongoose.Schema.Types.ObjectId,
            ref: 'User'
        },
        timestamp: Date
    },
    metadata: {
        blockedBy: {
            type: mongoose.Schema.Types.ObjectId,
            ref: 'User'
        },
        blockedReason: String,
        archivedAt: Date,
        archivedBy: {
            type: mongoose.Schema.Types.ObjectId,
